class Solution {
    public List<List<Integer>> threeSum(int[] nums) {
        HashSet<List<Integer>> output = new HashSet<>();

        int n = nums.length;
        Arrays.sort(nums);

        for(int i=0;i<n;i++){
            // a + b + c = 0; a+b = -c
            if(i!=0 && nums[i] == nums[i-1])
                continue;

            int temp = -1 * nums[i];
            HashSet<Integer> hs = new HashSet<>();
            for(int j=i+1;j<n;j++){
                int target = temp - nums[j];
                if(hs.contains(target)){
                    output.add(Arrays.asList(nums[i],nums[j],target));
                }
                else {
                    hs.add(nums[j]);
                }
            }
        }
        
        return new ArrayList<List<Integer>>(output);
    }
}

