class Solution {
    List<List<Integer>> output = new ArrayList<>();
    public List<List<Integer>> combinationSum(int[] candidates, int target) {
        dfs(candidates,target,0,0,new ArrayList<Integer>());
        return output;
    }

    public void dfs(int[] can, int target, int sum, int index, List<Integer> list){
        if(sum > target)
            return;
        if(sum == target){
            output.add(new ArrayList<>(list));
        }

        for(int i=index;i<can.length;i++){

            list.add(can[i]);
            dfs(can,target,sum+can[i],i,list);
            list.remove(list.size() -1);
        }
    }
}

class Solution {
    public int[][] merge(int[][] intervals) {
        Arrays.sort(intervals,(a,b) -> a[0] - b[0]);
        int n = intervals.length;
        int start = intervals[0][0];
        int end = intervals[0][1];

        List<int[]> output = new ArrayList<>();

        for(int i=1;i<n;i++){
            if(end < intervals[i][0]){
                output.add(new int[]{start,end});
                start = intervals[i][0];
                end = intervals[i][1];
            }
            else if(end != -1 && end >= intervals[i][0]){
                start = Math.min(start,intervals[i][0]);
                end = Math.max(end,intervals[i][1]);
            }
            else {
                output.add(intervals[i]);
            }
        }

        if(start != -1 && end != -1){
            output.add(new int[]{start,end});
        }

        int[][] out = new int[output.size()][2];

        for(int i=0;i<output.size();i++){
            out[i][0] = output.get(i)[0];
            out[i][1] = output.get(i)[1];
        }

        return out; 
    }
}

class Solution {
    public void sortColors(int[] nums) {
        int n = nums.length;
        int red = 0; // (color 0)
        int white = 0; // color 1
        int blue = n -1; // color 2

        while(white <= blue){
            if(nums[white] == 0){
                int temp = nums[red];
                nums[red] = nums[white]; // 0
                nums[white] = temp;
                red++;
                white++;
            }
            else if(nums[white] == 1){
                white++;
            }
            else {
                int temp = nums[blue];
                nums[blue] = nums[white];
                nums[white] = temp;
                blue--;
            }

        }
    }
}

class Solution {
    public int maxArea(int[] height) {
        int n = height.length;
        int i=0, j= n-1;
        int ans = Integer.MIN_VALUE;

        while(i<j){
            int h = Math.min(height[i],height[j]);
            ans = Math.max(ans,h*(j-i));
            if(height[i] < height[j]) i++;
            else j--;
        }
        return ans;
    }
}

class Solution {
    public int canCompleteCircuit(int[] gas, int[] cost) {
        int totalGas = 0;
        int totalCost =0;
        int n=gas.length;
        for(int i=0;i<n;i++){
            totalGas = totalGas + gas[i];
            totalCost = totalCost + cost[i];
        }

        if(totalCost > totalGas)
            return -1;
        int idx = 0;
        int runningSum =0;
        for(int i=0;i<n;i++){
            runningSum = runningSum + gas[i];
            if(runningSum - cost[i] < 0){
                idx = i+1;
                runningSum =0;
            }
            else {
                runningSum = runningSum - cost[i];
            }
        }
        return idx;
    }
}

class Solution {
    public int longestConsecutive(int[] nums) {
        Set<Integer> hs = new HashSet<>();
        for(int a: nums){
            hs.add(a);
        }
        int max =0;
        for(int n: hs){
            if(!hs.contains(n-1)){
                int currStreak = n + 1;
                
                while(hs.contains(currStreak)){
                    currStreak++;
                }
                
                max = Math.max(max,currStreak - n);
            }
        }
        
        return max;
    }
}

class Solution {
    public void rotate(int[] nums, int k) {
        int n = nums.length;
        int count=0;
        for(int curr=0;count < n;curr++){
            int startIndex = curr;
            int currIndex = curr;
            int prevValue = nums[curr];
            do {
                currIndex = (currIndex + k ) % n;
                int nextValue = nums[currIndex];
                nums[currIndex] = prevValue;
                prevValue = nextValue;
                count++;
            }while(startIndex != currIndex);

        }
    }
}

