class Solution {
    public int subarraySum(int[] nums, int k) {
        HashMap<Integer,Integer> map = new HashMap<>();
        int total =0, count =0;
        map.put(0,1);

        for(int n: nums){
            total = total + n;

            if(map.containsKey(total - k)){
                count = count + map.get(total -k);
            }

            map.put(total,map.getOrDefault(total,0)+1);
        }

        return count;
    }
}

