class Solution {
    public int minMeetingRooms(List<Interval> intervals) {
        usingheap(intervals);
        List<Integer> start = new ArrayList<>();
        List<Integer> end = new ArrayList<>();
        for(Interval i : intervals){
            start.add(i.start);
            end.add(i.end);
        }    

        Collections.sort(start);
        Collections.sort(end);

        int i=0,j=0,count=0,max=0;

        while(i<intervals.size()){
            if(start.get(i) < end.get(j)){
                count++;
                i++;
            }
            else {
                j++;
                count--;
            }

            max = Math.max(max,count);
            
        }
        return max;

        

    }
    
    public int usingheap(List<Interval> intervals){
        Collections.sort(intervals, (a,b) -> a.start - b.start);

        PriorityQueue<Integer> pq = new PriorityQueue<>();

        for(int i=0;i<intervals.size();i++){
            while(!pq.isEmpty() && intervals.get(i).end >= pq.peek() ){
                pq.poll();
            }
            pq.offer(intervals.get(i).end);
        }

        return pq.size();
    }
}

class Solution {
    public int threeSumClosest(int[] nums, int target) {
        int min = Integer.MAX_VALUE, val=0;

        Arrays.sort(nums);
        for(int i=0;i<nums.length;i++){
            int temp = nums[i];
            int j=i+1, k=nums.length-1;
            while(j<k){
                int m = temp + nums[j] + nums[k];
                if(min > Math.abs(target-m)){
                    min = Math.abs(target-m);
                    val = m;
                }
                
                if( m > target){
                    k--;
                }
                else {
                    j++;
                }
            }

        }

        return val;
    }
}

class Solution {
    public int eraseOverlapIntervals(int[][] intervals) {
        Arrays.sort(intervals,(a,b) -> a[0] - b[0]);
        int count =0;
        int end = intervals[0][1];
        for(int i=1;i<intervals.length;i++){
            if(end > intervals[i][0]){
                count++;
                end = Math.min(end,intervals[i][1]);
            }
            else {
                end = intervals[i][1];
            }
        }

        return count;
    }
}

class Solution {
    public int[] maxSlidingWindow(int[] nums, int k) {
        PriorityQueue<Integer> pq = new PriorityQueue<>((a, b) -> nums[b] - nums[a]);
        List<Integer> out = new ArrayList<>();
        for(int i=0;i<nums.length;i++){
            pq.add(i);
            while(!pq.isEmpty() && pq.peek() <= (i-k)){
                pq.poll();
            }
            if(i >= k - 1){
                out.add(nums[pq.peek()]);
            }
        }
        return out.stream().mapToInt(Integer::intValue).toArray();
    }
}

public List<Interval> employeeFreeTime(List<List<Interval>> schedule) {
       List<Interval> res = new ArrayList<>();
       List<Interval> intervals = new ArrayList<>();
       // Flattening the schedule
       for (List<Interval> employee : schedule)
           for (Interval interval : employee)
               intervals.add(interval);
       // Sorting by start of each Interval
       Collections.sort(intervals, (a, b) -> a.start - b.start);
       int end = intervals.get(0).end;
       // Checking for free time between intervals
       for (Interval interval : intervals) {
           if (interval.start > end)
               res.add(new Interval(end, interval.start));
           end = Math.max(end, interval.end);
       }
       return res;
   }

