class MinStack {

    Stack<Node> st = null;
    public MinStack() {
        st = new Stack<Node>();
    }
    
    public void push(int val) {
        Node n = new Node();
        n.val = val;
        if(st.isEmpty()){           
            n.min = val;            
        }
        else{
            Node temp = st.peek();
            if(temp.min < val){
                n.min = temp.min;
            }
            else{
                n.min = val;
            }
        }
        st.push(n);
        //System.out.println("Push:" +st.toString());
    }
    
    public void pop() {
        st.pop();
        //System.out.println("Pop:" +st.toString());
    }
    
    public int top() {
        Node n = st.peek();
        return n.val;
    }
    
    public int getMin() {
        Node n = st.peek();
        return n.min;
    }
}

