class MyStack {

    private Queue<Integer> q1;
    private Queue<Integer> q2;

    public MyStack() {
        q1 = new ArrayDeque<>();
        q2 = new ArrayDeque<>();
    }

    public void push(int x) {
        q1.add(x);
    }

    public int pop() {
        while (q1.size() > 1) {
            q2.add(q1.poll());
        }

        int poppedVal = q1.poll();
        Queue<Integer> temp = q1;
        q1 = q2;
        q2 = temp;

        return poppedVal;
    }

    public int top() {
        while (q1.size() > 1) {
            q2.add(q1.poll());
        }

        int topVal = q1.peek();
        q2.add(q1.poll());
        Queue<Integer> temp = q1;
        q1 = q2;
        q2 = temp;

        return topVal;
    }

    public boolean empty() {
        return q1.isEmpty();
    }
}

class Solution {
    public int[] dailyTemperatures(int[] temperatures) {
        int n = temperatures.length;
        int[] output = new int[n];
        Stack<Integer> st = new Stack<>();
        for(int i=n-1;i>=0;i--){
            while(!st.isEmpty() && temperatures[st.peek()] <= temperatures[i]){
                st.pop();
            }
            if(st.isEmpty())
                output[i] = 0;
            else 
                output[i] = st.peek() - i;

            st.push(i);
        }

        return output;
    }
}

class Solution {
    public int[] asteroidCollision(int[] asteroids) {
        
        int n = asteroids.length;
        Stack<Integer> st = new Stack<>();
        for(int i: asteroids){
            if( i > 0){
                st.push(i);
            }
            else {
                while(!st.isEmpty() && st.peek() > 0 && i < 0 && st.peek() < Math.abs(i)){
                    st.pop();
                }

                if(st.isEmpty() || st.peek() < 0){
                    st.push(i);
                }

                if(!st.isEmpty() && st.peek() == -1 * i){
                    st.pop();
                }
            }
        
        }

        int[] out = new int[st.size()];
            for(int i=st.size() -1;i>=0;i--){
                out[i] = st.pop();
            }

            return out;

        
    }
}

class Solution {
    public int calculate(String s) {
        Stack<Integer> num = new Stack<>();
        Stack<Character> op = new Stack<>();
        int number =0;
        s = s.trim();
        for(char c: s.toCharArray()){
            if( c == ' ')
                continue;
            
            if(Character.isDigit(c)){
                number = number * 10 + Character.getNumericValue(c);
            }
            else {
                System.out.println(c);
                while (!op.isEmpty() && precedence(op.peek()) >= precedence(c)) {
                    int b = number;
                    int a = num.pop();
                    number = eval(a, b, op.pop());
                }
                num.push(number);
                number = 0;
                op.push(c);
            }

            
        }
        // finalize
        while (!op.isEmpty()) {
            int b = number;
            int a = num.pop();
            number = eval(a, b, op.pop());
        }

        return number;
    }

    private int precedence(char c) {
        if (c == '*' || c == '/') return 2;
        return 1; // + or -
    }


    public int eval(int a, int b, char c){
        System.out.println("(a,b) with c->"+a+","+b+","+c);
        switch(c){
            case '+': return a+b;
            case '-': return a-b;
            case '*': return a*b;
            case '/': return a/b;
        }
        return 0;
    }
}

