class Solution {
    public int calculate(String s) {
        Stack<Integer> st = new Stack<>();
        int number =0, sign = 1, result = 0;

        for(char c: s.toCharArray()){
            if(Character.isDigit(c)){
                number = number * 10 + (c - '0');
            }
            else if(c == '+'){
                result = result + sign * number;
                sign = 1;
                number = 0;
            }
            else if(c == '-'){
                result = result + sign * number;
                sign = -1;
                number = 0;
            }
            else if( c == '('){
                st.push(result);
                st.push(sign);
                result = 0;
                sign = 1;
            }
            else if( c == ')'){
                result = result + sign * number;
                number = 0;
                result = result * st.pop();
                result = result + st.pop();
            }
        }

        if(number != 0){
            result = result + sign * number;
        }

        return result;
    }
}

class Solution {
    public int longestValidParentheses(String s) {
        Stack<Integer> st = new Stack<>();
        st.push(-1);
        int maxLength = 0;
        for(int i=0;i<s.length();i++){
            char c = s.charAt(i);
            if(c == '('){
                st.push(i);
            }
            else {
                st.pop();
                if(st.isEmpty()){
                    st.push(i);
                }
                else {
                    maxLength = Math.max(maxLength, i - st.peek());
                }
            }
        }

        return maxLength;

    }
}

class FreqStack {
    HashMap<Integer,LinkedList<Integer>> st;
    HashMap<Integer,Integer> map;
    int maxFreq=0; 

    public FreqStack() {
        st = new HashMap<Integer,LinkedList<Integer>>();
        map = new HashMap<Integer,Integer>();
        maxFreq=0;
    }
    
    public void push(int val) {
        int curr = map.getOrDefault(val,0);
        curr++;
        map.put(val,curr);
        if(!st.containsKey(curr)){
            st.put(curr, new LinkedList<Integer>());
        }
        st.get(curr).addFirst(val);
        maxFreq = Math.max(maxFreq,curr);
    }
    
    public int pop() {
        int ans = st.get(maxFreq).removeFirst();

        int currFreq = map.get(ans);
        currFreq--;
        map.put(ans,currFreq);
        if(st.get(maxFreq).size() == 0){
            maxFreq--;
        }
        return ans;
    }
}

