class Solution {
    public ListNode swapPairs(ListNode head) {
        
        ListNode temp = head, prev = null;

        while(temp != null && temp.next != null){
            ListNode first = temp;
            ListNode second = temp.next;
            ListNode next = second.next;

            second.next = first;
            first.next = next;

            if(prev != null){
                prev.next = second;
            }
            else{
                head = second;
            }
            prev = first;
            temp = first.next;
        }

        return head;
    }
}

