class Solution {
    public void reorderList(ListNode head) {
     int count = 0;
     ListNode temp = head;
     Stack<Integer> st = new Stack<>();
     while(temp != null){
        st.push(temp.val);
        temp = temp.next;
        count++;
     }

    temp = head;

    while(temp != null && st.size() > 0){
        ListNode next = temp.next;
        ListNode newNode = new ListNode(st.pop());
        temp.next = newNode;
        newNode.next = next;
        temp = temp.next.next;
    }

    temp = head;

    while(count>1){
        temp = temp.next;
        count--;
    }
    temp.next = null;


    }
}

