class LRUCache {
    HashMap<Integer,Node> cache;
    int capacity = 0;
    Node head;
    Node tail;

    public LRUCache(int capacity) {
        this.capacity = capacity;
        this.cache = new HashMap<Integer,Node>();
        this.head = new Node(-1,-1);
        this.tail = new Node(-1,-1);
        head.next = tail;
        tail.prev = head;
    }
    
    public int get(int key) {
        if(!cache.containsKey(key)){
            return -1;
        }

        Node n = cache.get(key);
        removeNode(n);
        addNode(n);
        return n.value;
    }
    
    public void put(int key, int value) {
        if(cache.containsKey(key)){
            // if  element exist, remove the node for doubly linkedlist
            Node entry = cache.get(key);
            removeNode(entry);
        }
        Node newEntry = new Node(key,value);
        cache.put(key,newEntry);
        addNode(newEntry);

        if(cache.size() > capacity){
            Node deleteCache = tail.prev;
            cache.remove(deleteCache.key);
            removeNode(deleteCache);
        }
    }

    public void addNode(Node node){
        Node next = head.next;
        head.next = node;
        node.next = next;
        next.prev = node;
        node.prev = head;
    }

    public void removeNode(Node node){
        Node next = node.next;
        Node prev = node.prev;
        prev.next = next;
        next.prev = prev;
    }
}

