class Solution {
    public ListNode reverseKGroup(ListNode head, int k) {
        int n =0;
        ListNode temp = head;
        while(temp != null){
            temp = temp.next;
            n++;
        }

        if(k > n){
            return head;
        }

        int t = n / k;

        ListNode dummy = new ListNode(-1);
        dummy.next = head;
        ListNode prevGroup = dummy;

        while(t>0){
            // first node of the group
            ListNode start = prevGroup.next;
            ListNode curr = start;
            ListNode prev = null;
            ListNode next = null;
            int count = k;
            while (count > 0){
                next = curr.next;
                curr.next = prev;
                prev = curr;
                curr = next;
                count--;
            }

            prevGroup.next = prev;
            start.next = curr;
            prevGroup = start;
            t--;
        }

        return dummy.next;
    }
}

class Solution {
