    public int[][] updateMatrix(int[][] mat) {
        
        int n = mat.length;
        int m = mat[0].length;
        int[][] visited = new int[n][m];

        Queue<int[]> q = new LinkedList<>();

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(mat[i][j] == 0){
                    q.add(new int[]{i,j,0});
                    visited[i][j] = 1;
                }
            }
        }

        int[] row = {-1,0,1,0};
        int[] col = {0,1,0,-1};

        while(!q.isEmpty()){
            int[] curr = q.poll();

            for(int i=0;i<4;i++){
                int r = row[i] + curr[0];
                int c = col[i] + curr[1];

                if( r>=0 && r < n && c >=0 && c < m && visited[r][c] != 1){
                    visited[r][c] = 1;
                    mat[r][c] = curr[2] + 1;
                    q.add(new int[]{r,c,mat[r][c]});
                }
            }
        }

        return mat;
        
    }
