class Solution {
    public int numIslands(char[][] grid) {
        int n= grid.length;
        int m = grid[0].length;

        boolean[][] v = new boolean[n][m];
        int count =0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(grid[i][j] == '1' && !v[i][j]){
                    dfs(grid,v,n,m,i,j);
                    count++;
                }
            }
        }

        return count;
    }

    public void dfs(char[][] grid, boolean[][] visited, int n, int m, int i, int j){
        if(i < 0 || j < 0 || i >= n || j >= m || grid[i][j] != '1'){
            return;
        }

        if(visited[i][j] == true)
            return;
        
        visited[i][j] = true;

        dfs(grid,visited,n,m,i-1,j);
        dfs(grid,visited,n,m,i,j+1);
        dfs(grid,visited,n,m,i+1,j);
        dfs(grid,visited,n,m,i,j-1);
    }
}

class Solution {
    public int orangesRotting(int[][] grid) {
        int n = grid.length, m = grid[0].length;
        boolean[][] visited = new boolean[n][m];
        int freshOrangePresent = 0;
        Queue<int[]> queue = new LinkedList<>();

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(grid[i][j] == 2){
                    queue.add(new int[]{i,j});
                    visited[i][j] = true;
                }
                else if(grid[i][j] == 1){
                    freshOrangePresent = 1;
                }
            }
        }
        
        // No rotten oranges
        if(queue.isEmpty())
            return freshOrangePresent == 1 ? -1 : 0;
        
        int[] row = {0,1,0,-1};
        int[] col = {1,0,-1,0};
        int count = 0;
        while(!queue.isEmpty()){
            int size = queue.size();            
            
            for(int k=0;k<size;k++){
                int[] curr = queue.peek();
                queue.remove();
                for(int i=0;i<4;i++){
                    int r = curr[0] + row[i];
                    int c = curr[1] + col[i];
                    if(r >= 0 && r < n && c >=0 && c < m && visited[r][c] == false && grid[r][c] == 1){
                        grid[r][c] = 2;
                        visited[r][c] = true;
                        queue.add(new int[]{r,c});
                    }
                }
                
            }
            count++;
        }

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(grid[i][j] == 1)
                    return -1;
            }
        }

        return count - 1;
    }
}

class Solution {
    public boolean exist(char[][] board, String word) {
        int n = board.length;
        int m = board[0].length;
        boolean[][] v = new boolean[n][m];
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(word.charAt(0) == board[i][j]){
                    if(dfs(board,v,word,0,n,m,i,j)){
                        return true;
                    }
                    
                }
            }
        }

        return false;
    }

    public boolean dfs(char[][] b, boolean[][] v, String word, int index, int n, int m, int i, int j){
        if(i < 0 || j < 0 || i >= n || j >= m || v[i][j] || word.charAt(index) != b[i][j]){
            return false;
        }

        if(index == word.length() - 1){
            return true;
        }

        v[i][j] = true;
        
        boolean found = dfs(b,v,word,index+1,n,m,i-1,j) || dfs(b,v,word,index+1,n,m,i,j+1) || dfs(b,v,word,index+1,n,m,i+1,j) || dfs(b,v,word,index+1,n,m,i,j-1); 

        v[i][j] = false;
        return found;
    }
}

class UnionFind {
    int[] parent;
    int[] size;

    public UnionFind(int n){
        parent = new int[n];
        size = new int[n];
        for(int i=0;i<n;i++){
            parent[i] = i;
            size[i] = 1;
        }
    }

    public int findParent(int n){
        if(parent[n] == n)
            return n;
        parent[n] = findParent(parent[n]);
        return parent[n];
    }

    public void union(int a, int b){
        int parentA = findParent(a);
        int parentB = findParent(b);

        if(size[parentA] > size[parentB]){
            parent[parentB] = parentA;
            size[parentA] = size[parentB] + size[parentA];
        }
        else{

            parent[parentA] = parentB;
            size[parentB] = size[parentB] + size[parentA];

        }
    }
}

class Solution {
    public List<List<String>> accountsMerge(List<List<String>> accounts) {
        int n = accounts.size();

        UnionFind uf = new UnionFind(n);

        HashMap<String,Integer> emaildsToNameId = new HashMap<>();


        // here we are creating map of emails to user (represented at user index) 
        // Also, if email already exist that means same person, so we are doing union for it.
        for(int i=0;i<n;i++){
            List<String> emails= accounts.get(i);
            // first index is name and rest is email
            for(int j=1;j<emails.size();j++){
                String email = emails.get(j);

                if(emaildsToNameId.containsKey(email)){
                    uf.union(i,emaildsToNameId.get(email));
                }
                else {
                    emaildsToNameId.put(email,i);
                }
            }
        }

        HashMap<Integer,List<String>> idsToEmail = new HashMap<>();

        for(String key: emaildsToNameId.keySet()){
            int parent = uf.findParent(emaildsToNameId.get(key));
            
            if(idsToEmail.containsKey(parent)){
                idsToEmail.get(parent).add(key);
            }
            else {
                List<String> em = new ArrayList<>();
                em.add(key);
                idsToEmail.put(parent,em);
            }
        }

        List<List<String>> output = new ArrayList<>();

        for(int key: idsToEmail.keySet()){
            List<String> list = idsToEmail.get(key);
            Collections.sort(list);
            list.add(0,accounts.get(key).get(0));
            output.add(list);
        }

        return output;

    }
}

