            for(int j=0;j<m;j++)
                memo[i][j] = -1;

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                ans = Math.max(ans,dfs(matrix,i,j,n,m));
            }
        }

        return ans;
    }

    public int dfs(int[][] mat, int i, int j, int n, int m){

        if(memo[i][j] != -1)
            return memo[i][j];
        
        int[] row = {-1,0,1,0};
        int[] col = {0,-1,0,1};
        int maxlen = 1;
        for(int k=0;k<4;k++){
            int r = i + row[k];
            int c = j + col[k]; 
            if(r>=0 && r<n && c >=0 && c < m && mat[i][j] < mat[r][c])
                maxlen = Math.max(maxlen, 1 + dfs(mat,r,c,n,m));
        }
        memo[i][j] = maxlen;
        return maxlen;

    }
}

class Solution {
    public int numBusesToDestination(int[][] routes, int source, int target) {
        if(source == target)
            return 0;
        
        List<HashSet<Integer>> map = new ArrayList<>();
        Queue<int[]> q = new LinkedList<>();
        boolean[] visited = new boolean[routes.length];
        for(int i=0;i<routes.length;i++){
            HashSet<Integer> hs = new HashSet<>();
            for(int j=0;j<routes[i].length;j++){
                hs.add(routes[i][j]);
            }
            if(hs.contains(source)){
                // bus number and number of buses;
                q.add(new int[]{i,1});
                visited[i] = true;
            }
            map.add(hs);
        }

        if(q.isEmpty())
            return -1;

        int min = Integer.MAX_VALUE;

        while(!q.isEmpty()){
            int[] curr = q.poll();

            if(map.get(curr[0]).contains(target)){
                min = Math.min(min,curr[1]);
            }

            for(int i: map.get(curr[0])){
                for(int j=0;j<map.size();j++){
                    if(map.get(j).contains(i) && !visited[j]){
                        visited[j] = true;
                        q.add(new int[]{j,curr[1] +  1});
                    }
                }
            }

        }
        return min == Integer.MAX_VALUE ? -1 : min;
    }
}

