        dp[1] = 1;
        for(int i=2;i<=n;i++){
            dp[i] = dp[i-1] + dp[i-2]; 
        }

        return dp[n];
    }

    public int recur(int n){
        if(n == 0)
         return 1;
        
        if(n < 0)
            return 0;

        return recur(n-1) + recur(n-2);
    }
}


