class Solution {
    HashMap<Integer,Integer> map = new HashMap<>();
    public int coinChange(int[] coins, int amount) {
        if (amount == 0)
            return 0;
        
        int value = recur(coins,amount);
        return value >= 1e9 ? -1 : value;
    }

    public int recur(int[] coins, int amount){
        if(amount == 0)
            return 0;
        
        if(map.containsKey(amount))
            return map.get(amount);

        int count = (int) 1e9;
        for(int i: coins){
            if(amount - i >= 0){
                count = Math.min(count, 1 + recur(coins,amount - i));                
            }
        }
        map.put(amount,count);
        return count;
    }
}

class Solution {
    public int maxProduct(int[] nums) {
        int n = nums.length;

        int prefix = 0, suffix = 0;
        int max_product = nums[0];

        for(int i=0;i<n;i++){
            prefix = nums[i] * (prefix == 0 ? 1 : prefix);
            suffix = nums[n - i - 1] * (suffix == 0 ? 1 : suffix);

            max_product = Math.max(max_product, Math.max(prefix,suffix));
        }

        return max_product;
    }
}

class Solution {
    public boolean wordBreak(String s, List<String> wordDict) {
        boolean[] dp = new boolean[s.length() + 1];
        dp[s.length()] = true;
        for(int i=s.length() -1;i>=0;i--){
            String curr = s.substring(i,s.length());
            for(int j=0;j<wordDict.size();j++){
                if(wordDict.get(j).length() <= curr.length() && curr.startsWith(wordDict.get(j))){
                    dp[i] = dp[i+wordDict.get(j).length()];   
                }
                if(dp[i])
                    break;
            }
        }
        return dp[0];
    }

    public boolean dfs(List<String> word, String s, int index, Boolean[] dp){
        if(index == s.length()){
            return true;
        }

        if(dp[index] != null){
            return dp[index];
        }
        
        for(int i=0;i<word.size();i++){
            String curr = s.substring(index,s.length());
            if(word.get(i).length() <= curr.length() && curr.startsWith(word.get(i)) && dfs(word,s,index + word.get(i).length(),dp)){
                return true;
            }
        }

        dp[index] = false;
        return false;
    }
}

class Solution {
    public int lengthOfLIS(int[] nums) {
        int[][] dp = new int[nums.length][nums.length + 1];
        for(int[] d : dp)
            Arrays.fill(d,-1);
        return dfs(nums,0,-1,dp);
    }

    public int dfs(int[] nums, int index, int preIndex, int[][] dp){
        if(index == nums.length)
            return 0;

        if(dp[index][preIndex + 1] != -1)
            return dp[index][preIndex + 1];

        int take = 0;
        if(preIndex == -1 || nums[index] > nums[preIndex]){
            take = 1 + dfs(nums,index+1,index, dp);
        }

        int notTake = dfs(nums,index+1,preIndex,dp);
        
        dp[index][preIndex + 1] = Math.max(take,notTake); 
        return dp[index][preIndex + 1];
    }
}

